INSERT INTO `courses` (`id`, `title`, `slug`, `description`, `price`, `course_image`, `start_date`, `published`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'Bahasa Inggris', 'bahasa inggris', 'Disini kamu akan belajar banyak materi bahasa inggris untuk SMA seperti materi expression, grammar , tenses sampai descriptive texts. \r\n\r\nSelain itu kamu juga bisa download kumpulan  soal dalam bentuk pdf di setiap bab, sehingga kamu bisa mempelajari dari bab tersebut lebih dalam.', '10.00', '1513395088-rsz_html.jpg', '2017-12-17', 1, '2017-12-15 01:55:03', '2017-12-17 01:42:32', '2017-12-17 01:42:32'),
(2, 'Javascript', '', 'JavaScript adalah bahasa pemograman yang digunakan untuk membuat interaksi dan menambah fitur dinamis ke dalam halaman web. Untuk membuat website, memang tidak harus menggunakan JavaScript. Namun saat ini penggunaan JavaScript hampir selalu ada di setiap web modern. Bagi programmer web, JavaScript merupakan salah satu bahasa pemograman web yang wajib di kuasai. Duniailkom menyediakan artikel lengkap yang dapat digunakan sebagai tutorial dasar dalam mempelajari JavaScript. Tutorial ini akan diurutkan secara sistematis, dan terdiri dari 3 bagian: Tutorial JavaScript Dasar, Tutorial JavaScript Lanjutan, dan Artikel JavaScript.\r\n\r\n', '10.00', NULL, '2017-12-24', 1, '2017-12-15 01:55:03', '2017-12-17 01:42:32', '2017-12-17 01:42:32'),
(3, 'MySQL', '', 'MySQL merupakan salah satu aplikasi database paling populer di dunia, terutama dalam pengembangan web. Hampir setiap proyek pembuatan website menggunakan MySQL, termasuk berbagai CMS (Content Management System) seperti WordPress, Joomla, Drupal, dll menggunakan MySQL. MySQL merupakan aplikasi wajib yang harus dikuasai oleh programmer web. Karena hal inilah disini kita mencoba membuat beberapa tutorial dasar MySQL.\r\n\r\nTutorial dasar ini ditujukan untuk pemula yang ingin mempelajari MySQL dari awal. Kita akan membahas sedikit teori database, seperti pengenalan tabel, relasi, kolom/field, baris/record, serta pengertian RDBMS (Relational Database Management Systems). Selanjutnya kita akan menginstall aplikasi MySQL dan membahas query-query penting yang diperlukan untuk administrasi database.', '10.00', NULL, '2017-12-23', 1, '2017-12-15 01:55:03', '2017-12-17 01:42:32', '2017-12-17 01:42:32'),
(4, 'PHP', '', 'PHP merupakan bahasa wajib programmer web. Menurut wikipedia, PHP digunakan pada lebih dari 75% web server di seluruh dunia. \r\n\r\nTutorial Belajar PHP ini cocok untuk pemula yang ingin menguasai PHP, maupun yang sudah mahir sebagai penyegaran dan bahan referensi. Untuk belajar PHP, anda dianggap telah mengetahui sedikit tentang HTML. Karena pada dasarnya PHP digunakan untuk memanipulasi HTML agar bisa tampil dinamis.\r\n\r\nPengetahuan tentang MySQL, CSS dan Javascript juga akan membantu dalam memahami PHP, walaupun ini bukan hal yang mutlak. Jika belum menguasai HTML dan CSS, silahkan mempelajarinya pada Tutorial Belajar HTML, dan Tutorial Belajar CSS.', '10.00', NULL, '2017-12-24', 1, '2017-12-15 01:55:03', '2017-12-17 01:42:33', '2017-12-17 01:42:33'),
(5, 'Jquery ', '', 'Hampir semua situs-situs besar menggunakan jQuery untuk memudahkan proses penulisan kode JavaScript. Maka dari itu seorang programmer web juga dituntut untuk bisa menguasai Jquery.  ', '10.00', NULL, '2017-12-24', 1, '2017-12-15 01:55:04', '2017-12-17 01:42:33', '2017-12-17 01:42:33'),
(6, 'Matematika', 'Matematika', 'materi  matematika untuk anak SMA', '1.00', '1513499693-rsz_html.jpg', '2017-12-17', 1, '2017-12-17 01:34:53', '2017-12-17 01:42:33', '2017-12-17 01:42:33'),
(7, 'Bahasa Inggris', 'Bahasa Inggris', 'Pada materi bahasa inggris yang ditujukan untuk siswa SMA ini kami ingin memberikan rangkuman materi pelajaran bahasa inggris untuk SMA', '1.00', '1513500249-greeting.jpg', '2017-12-17', 1, '2017-12-17 01:44:09', '2017-12-17 01:46:16', NULL),
